app.controller("ReportListCtrl", function ($scope, $log, $sce, $reports, $filter, $modal, cacheSvc, $parse) {//

    var d = new Date();
    var data = $scope.$parent.window.data;

    var names = [];
    var references = [];
    var rows = [];

    var header = "";
    if (data && data.header) {
        header = data.header;
    }

    if (data && data.ids) {
        var asc = {};
        for (var i = 0; i < data.ids.length; i++) {
            var id = data.ids[i];
            var row = cacheSvc.get(id);
            rows.push(row);
            //
            var name = $parse('cart.name')(row) + (row.name ? ": " + row.name : "");
            var refs = $parse('cart.references')(row);
            if (refs) {
                for (var j = 0; j < refs.length; j++) {
                    var ref = refs[j];
                    if (!asc[ref]) {
                        asc[ref] = 1;
                        references.push(ref);
                    }
                }
            }
            
            names.push(name || row.id);
        }
    }

    /*
    var model = $scope.$parent.model;
    /*/
    var model = {
        window: $scope.$parent.window,
        idleCounter: 0,
        waitCounter: 0,
        doneCounter: 0,
        errorCounter: 0,
        only1: false,
        state: 'idle',

        data: data,
        names: names,
        modal: undefined,
        header: header,
        //autoclose: function () {
        //    return (!data || !data.ids || data.ids.length == 0);
        //}
    }
    // */

    var wrap = function (report) {
        //
        delete report.template;

        //State
        report.state = "idle";
        report.wait = false;
        report.success = true;

        //Range
        var s = new Date();
        var e = new Date();


        s.setHours(0, 0, 0, 0);
        e.setMinutes(0, 0, 0);

        if (report.range == "Day") {
            e.setDate(e.getDate() - 1);
            e.setHours(23, 59, 59, 999);
            s.setDate(1);
            if (s >= e) {
                s.setMonth(s.getMonth() - 1);
            }
        }

        report.start = s;
        report.end = e;

        report.ids = [];
        for (var i = 0; i < rows.length; i++) {
            var row = rows[i];
            var add = false;
            if (report.target && report.target != "Common" && report.target != "Single" && row.cart && row.cart.references) {
                for (var j = 0; j < row.cart.references.length; j++) {
                    var ref = row.cart.references[j];
                    if (ref == report.target) {
                        add = true;
                        break;
                    }
                }
            } else {
                add = true;
            }
            if (add) {
                report.ids.push(row.id);
            }
        }

        //
        report.selectable = (report.target == "Single" || report.ids.length > 0);

        //
        report.update = function () {
            if (model.data) {
                model.selected.state = "wait";
                //$log.debug("даты отчета %s %s", start.toString(), end.toString());
                $reports.build(report.id, report.start, report.end, report.ids).then(function (r) {
                    report.wait = false;
                    $log.debug("строим отчет report=%s", $filter('json')(r));
                    if (!r) {
                        report.state = "error";
                        report.error = "Отчет не загружен";
                        report.success = false;
                    } else {
                        report.state = "success";
                        report.error = "";
                        report.success = true;
                        report.reportAsHtml = $sce.trustAsHtml(r);
                        report.reportAsText = r;
                    }
                }, function (err) {
                    report.state = "error";
                    report.error = "Отчет не построен: " + (err || "неизвестная ошибка");
                    report.success = false;
                    report.wait = false;
                    $log.error("отчет не построен: %s", err);
                });
                //}
            }
        }

        return report;
    }

    model.savePdf = function (reportAsText) {
        if (reportAsText) {
            $reports.exportToPdf(reportAsText.toString());
        }
    };

    model.toExcel = function (reportAsText) {
        if (reportAsText) {
            $reports.exportToXls(reportAsText.toString());
        }
    };

    $scope.$watch('model.reports', function () {
        model.idleCounter = 0;
        model.waitCounter = 0;
        model.doneCounter = 0;
        model.errorCounter = 0;
        if (!model.reports) return;

        for (var i = 0; i < model.reports.length; i++) {
            var report = model.reports[i];
            report.done = false;
            switch (report.state) {
                case "idle":
                    model.idleCounter++;
                    break;
                case "wait":
                    model.waitCounter++;
                    break;
                case "success":
                    report.done = true;
                    model.doneCounter++;
                    break;
                default:
                    model.errorCounter++;
                    break;
            }
        }

        if (model.errorCounter > 0) {
            model.state = "error";
        } else if (model.waitCounter > 0) {
            model.state = "wait";
        } else if (model.doneCounter > 0) {
            model.state = "success";
        } else {
            model.state = "idle";
        }
    }, true);


    var init = function () {
        if (model.selected && model.selected.id) {//save selected
            model.selectedId = model.selected.id;
            delete model.selected;
        }
        delete model.reports;
        ////
        $reports.all().then(function (data) {
            model.reports = [];
            for (var i = 0; i < data.reports.length; i++) {
                var report = data.reports[i];
                model.reports.push(wrap(report));
            }
            model.sorted = $filter('orderBy')(model.reports, 'name');

            //select
            if (model.reports.length == 0) {
                //none
            } else if (model.reports.length == 1) {
                model.select(model.reports[0], true);
            } else if (model.selectedId) {
                //restore selected
                for (var i = 0; i < model.reports.length; i++) {
                    var d = model.reports[i];
                    if (d.id == model.selectedId) {
                        model.select(d, true);
                        break;
                    }
                }
                if (i == model.reports.length) {
                    delete model.only1;
                }
            } else {
                var sel;
                for (var i = 0; i < model.sorted.length; i++) {
                    var r = model.sorted[i];
                    if (r.selectable) {
                        sel = r;
                        break;
                    }
                }
                if (sel) {
                    for (var i = 0; i < model.reports.length; i++) {
                        var r = model.reports[i];
                        if (r.id == sel.id) {
                            model.select(model.reports[i], false);
                            break;
                        }
                    }
                }
            }
        });
    };

    model.select = function (report, update) {
        if (report.selectable) {
            model.selected = report;
            if (update && report.update && report.state == "idle") {
                report.update();
            }
        }
    };

    init();


    if (data && data.reportId) {
        model.selectedId = data.reportId;
        model.only1 = true;
    }

    //$scope.reportId = reportSvc.getReportId();


    //model.startOpen = function ($event) {
    //    $event.preventDefault();
    //    $event.stopPropagation();

    //    model.startOpened = true;
    //};

    //model.endOpen = function ($event) {
    //    $event.preventDefault();
    //    $event.stopPropagation();

    //    model.endOpened = true;
    //};

    //modal

    model.modalOpen = function (newData) {

        if (newData && newData.reportId) {
            model.selectedId = newData.reportId;
            model.only1 = false;
            for (var i = 0; i < model.reports.length; i++) {
                var d = model.reports[i];
                if (d.id == model.selectedId) {
                    model.select(d, true);
                    break;
                }
            }
        }

        model.modal = $modal.open({
            templateUrl: model.window.modalTemplateUrl,
            windowTemplateUrl: model.window.windowTemplateUrl,
            size: 'lg',
            scope: $scope
        });
        model.modalIsOpen = true;

        model.modal.result.then(function () {
            model.modalIsOpen = false;
            if (model.autoclose && model.autoclose()) {
                model.close();
            }
        }, function () {
            model.modalIsOpen = false;
            if (model.autoclose && model.autoclose()) {
                model.close();
            }
        });
    }

    model.toggleSideList = function () {
        model.only1 = !model.only1;
    }

    model.close = function () {
        model.modal.close();
        model.window.close();
    }

    model.window.open = model.modalOpen;

    model.modalOpen();

    //

    $scope.model = model;
});


