app.service("$folders", function ($rootScope, $transport) {

    var self = this;
    self.root = {};
    self.selection = [];

    var wrap = function (root) {
        root.data.type = "Folder";
        if (!root.data.id) {
            root.data.id = "root";
            root.data.type = "all";
            root.data.name = "Всё";
        }

        if (root.children) {
            for (var i = 0; i < root.children.length; i++) {
                wrap(root.children[i]);
            }
        }

        return root;
    };

    var resolve = function () {
        return $transport.send(new Message({ what: "folders-get" }))
			.then(function (message) {
			    self.root = wrap(message.body.root);
			    return self.root;
			});
    }

    var getRoot = function () {
        return self.root;
    }

    var selected = function () {
        return self.selection;
    }

    var init = function () {
        self.root = {};
        self.selection.length = 0;
    }

    $rootScope.$on("auth:deauthorized", function (e, message) {
        init();
    });

    self.selectedIds = [];
    var getSelectedIds = function () {
        return self.selectedIds;
    }

    var setSelectedIds = function (selectedIds) {
        self.selectedIds = selectedIds;
    }

    return {
        selected: selected,
        getRoot: getRoot,
        resolve: resolve,
        setSelectedIds: setSelectedIds,
        getSelectedIds: getSelectedIds
    }
});
