function SaveRule(action, target, content) {
    var self = this;
    self.action = action;
    self.target = target;
    self.content = content;
    return self;
}

app.controller("FolderEditCtrl", function ($uibModalInstance, $scope, data, $users, $log, $parse, $transport, $helper) {

    var model = {
        data: data,
        roots: [],
        folder: undefined,
        folderRoot: undefined,
        deleteEnable: false,
        //
        overlayEnabled: false,
        overlayText: "",
        overlay: $helper.overlayFunc
        //function (promise, text) {
        //return (function (m) {
        //    m.overlayText = text || "Загрузка...";
        //    m.overlayEnabled = true;
        //    return promise.finally(function () {
        //        m.overlayEnabled = false;
        //        m.overlayText = "";
        //    })
        //})(this);
        //}
    };

    var id = model.data.id;
    var isNew = model.data.isNew;

    model.update = function () {
        model.deleteEnable = false;
        model.folderRoot = undefined;
        model.folder = undefined;
        //
        model.overlay(
            $transport.send(new Message({ what: "edit-get-folder" }, { isNew: isNew, id: id || null })).then(function (message) {
                if (isNew) {
                    model.folderRoot = message.body.folder;
                    model.folder = message.body.folderNew;
                } else {
                    model.folder = message.body.folder;
                    //can be deleted or not
                    $transport.send(new Message({ what: "rows-get-ids" }, { filter: { text: "", groups: [id] } }))
                    .then(function (message) {
                        if (message.head.what == "rows-get-ids") {
                            var ids = $parse('body.ids')(message) || [];
                            if (ids.length == 0) {
                                model.deleteEnable = true;
                            }
                        }
                    });
                }
                model.folderRootName = $parse('folderRoot.name')(model);
                model.folderName = $parse('folder.name')(model);
            }).catch(function (err) {

            })
        );
    }

    model.update();


    model.save = function () {
        var rules = [];

        if (isNew) {
            rules.push(new SaveRule("add", "node", { id: model.folder.id, type: "Folder", body: model.folder }));
            if (model.folderRoot) {
                rules.push(new SaveRule("add", "relation", { start: model.folderRoot.id, end: model.folder.id, type: "contains", body: {} }));
            }
        } else {
            rules.push(new SaveRule("upd", "node", { id: model.folder.id, type: "Folder", body: model.folder }));
        }

        if (rules.length > 0) {
            model.overlay($transport.send(new Message({ what: "edit" }, { rules: rules })).finally(model.close), "Сохранение...");
        } else {
            model.close();
        }
    };

    model.delete = function () {
        var rules = [];

        //rules.push(new SaveRule("del", "relation", { start: , end: model.folder.id, type: "create", body: {} }));
        rules.push(new SaveRule("del", "node", { id: model.folder.id, type: "Folder", body: model.folder }));

        if (rules.length > 0) {
            model.overlay($transport.send(new Message({ what: "edit" }, { rules: rules })).finally(model.close), "Удаление...");
        } else {
            model.close();
        }
    }

    model.close = function () {
        $uibModalInstance.close();
    }

    $scope.model = model;
});
