app.service("$list", function ($transport, $rootScope, $log, $q, $parse, cacheSvc, $helper, $timeout, $folders) {

    /////////////////// SERVICE /////////////////// SERVICE /////////////////// SERVICE /////////////////// SERVICE /////////////////// SERVICE 
    //текущий список
    //хранит в себе: список ид, список выделенных, применённый фильтр, список пинов

    var service = this;

    service.filter = "";//применённый фильтр
    service.groups = [];//группы
    service.list = []; //список tube id - строки, с учетом пиннинга и фильтра 
    service.sels = []; //выделенные строки
    service.pins = []; //список пинов
    service.isPinnedOnly = false;

    var what = Object.prototype.toString;

    var toWrap = [];
    var wrapTimeout;

    var wrapped = [];
    var wrappedTimeout;

    var carts = {};
    var refcache = {};

    //Описание маленькой тележки:
    //cart: {name: <имя Area>, done: <закончен ли wrapping?>, status: { это - копия status'а основного объекта }, cache: {Abnormal: [], Day: [], ... - копии cache основного объекта с обработкой}}

    var getCart = function (id) {
        if (!carts[id]) {
            var cart = {
                name: id,
                done: false,
                status: {},
                cache: {
                    Constant: [],
                    Current: [],
                    Hour: [],
                    Day: [],
                    Abnormal: [],
                    Gsm: []
                },
                references: []
            };
            carts[id] = cart;
        }
        return carts[id];
    }

    var getReferences = function (id) {
        if (!refcache[id]) {
            var refs = {}
            refcache[id] = refs;
        }
        return refcache[id];
    }

    var wrapWithDelay = function (row) {
        if (row && row.id) {
            if (!row.cart) {
                row.cart = getCart(row.id);
            }
            if (!row.ids) {
                row.ids = getReferences(row.id);
            }
            toWrap.push(row.id);
            $timeout.cancel(wrapTimeout);
            wrapTimeout = $timeout(wrapDelayed, 10);
        }
        return row;
    }

    var wrap = function (row) {
        if (row) {
            // создаем "маленькую тележку", в которой будут находиться результаты вычислений и ссылки на внутренние объекты "вагона"
            if (!row.cart) {
                row.cart = getCart(row.id);
            }
            if (!row.ids) {
                row.ids = getReferences(row.id);
            }
            //clear references
            for (var key in row.ids) {
                if (row.ids.hasOwnProperty(key)) {
                    delete row.ids[key];
                }
            }

            //pin
            for (var i = 0; i < service.pins.length; i++) {
                var pin = service.pins[i];
                if (pin.id == row.id) {
                    row.pinned = true;
                    break;
                }
            }
            // 
            if (row.Area && row.Area.length > 0 && row.Area[0].name) {
                row.cart.name = row.Area[0].name;
            }
            // row.cart-STATUS
            if (row.isDisabled) {//выключен - статус соответствующий
                var disabledHistory;
                var comment;
                var date;
                if ($helper.isString(row.disabledHistory)) {
                    disabledHistory = JSON.parse(row.disabledHistory);
                } else if ($helper.isArray(row.disabledHistory)) {
                    disabledHistory = row.disabledHistory;
                }

                if (disabledHistory && disabledHistory.length > 0) {
                    var history = disabledHistory[disabledHistory.length - 1];
                    comment = history.reason;
                    date = history.start;
                }

                var status = {
                    state: "disabled",
                    comment: comment,
                    date: date
                };
                row.cart.status = status;
            } else if (row.State) {//включен
                var lastState = (typeof (row.State) === "string") ?
                    JSON.parse(row.State) : row.State[row.State.length - 1];
                row.cart.status = lastState;
            } else if (row.cache && row.cache.State) {
                $helper.copyToFrom(row.cart.status, row.cache.State);
            }
            //find ids within rows => associative array id->node
            row.ids[row.id] = row;
            for (var key in row) {
                if (row.hasOwnProperty(key) && what.call(row[key]) === "[object Array]") {
                    for (var j in row[key]) {
                        var elem = row[key][j];
                        if (elem && elem.id) {
                            row.ids[elem.id] = elem;
                        }
                    }
                }
            }

            //CACHE
            if (row.cache && row.cache != null) {

                //Параметры - Связь
                var gsmLevel;
                var gsmBalance;
                {
                    var c = row.cache["Day"]
                    if (c && what.call(c) === "[object Array]") {
                        for (var i = 0; i < c.length; i++) {
                            var par = c[i];
                            if (par) {
                                switch (par.s1) {
                                    case "__Gsm":
                                        gsmLevel = {
                                            date: par.date,
                                            header: "Уровень сигнала",
                                            content: '<img src="img/' + $helper.getSignalImg(par.d1) + '"  width="20" /> ' + ((par.d1 == -1) ? 'н/д' : par.d1 + '%')
                                        }
                                        var bal = par.s2.replace(',', '.').match(/[0-9,\.]+/);
                                        gsmBalance = {
                                            date: par.date,
                                            header: "Баланс",
                                            content: (bal != null && bal.length > 0) ? parseFloat(bal[0]) + " руб." : par.s2
                                        }
                                        break;
                                    case "__GsmLevel":
                                        gsmLevel = {
                                            date: par.date,
                                            header: "Уровень сигнала",
                                            content: '<img src="img/' + $helper.getSignalImg(par.d1) + '"  width="20" /> ' + ((par.d1 == -1) ? 'н/д' : par.d1 + '%')
                                        }
                                        break;
                                    case "__GsmBalance":
                                        gsmBalance = {
                                            date: par.date,
                                            header: "Баланс",
                                            content: parseFloat(par.s2.replace(',', '.').match(/[0-9,\.]+/)[0]) + " руб."
                                        }
                                        //    
                                        break;
                                }
                            }
                        }
                    }
                }
                //<table class="table table-hover">
                //    <tr>
                //        <td></td>
                //        <td>
                //            
                //        </td>
                //    </tr>
                //    <tr>
                //        <td></td>
                //        <td>{{model.data.cart.gsm.balance}}</td>
                //    </tr>
                //</table>

                //LINK
                //row.cart.cacheLink = row.cache;

                //FILTERED COPY
                row.cart.cache.Constant.length = 0;
                row.cart.cache.Current.length = 0;
                row.cart.cache.Hour.length = 0;
                row.cart.cache.Day.length = 0;
                row.cart.cache.Abnormal.length = 0;
                row.cart.cache.Gsm.length = 0;

                if (row.cache.Constant) {//bypass
                    row.cart.cache.Constant = row.cache.Constant;
                }
                if (row.cache.Current) {//filter           
                    for (var i = 0; i < row.cache.Current.length; i++) {
                        var obj = row.cache.Current[i];
                        if (obj.s1 && obj.s1.search("__") == -1) {
                            row.cart.cache.Current.push(obj);
                        }
                    }
                }
                if (row.cache.Hour) {//filter           
                    for (var i = 0; i < row.cache.Hour.length; i++) {
                        var obj = row.cache.Hour[i];
                        if (obj.s1 && obj.s1.search("__") == -1) {
                            row.cart.cache.Hour.push(obj);
                        }
                    }
                }
                if (row.cache.Day) {//filter       
                    for (var i = 0; i < row.cache.Day.length; i++) {
                        var obj = row.cache.Day[i];
                        if (obj.s1 && obj.s1.search("__") == -1) {
                            row.cart.cache.Day.push(obj);
                        }
                    }
                }
                if (row.cache.Abnormal) {//bypass
                    row.cart.cache.Abnormal = row.cache.Abnormal;
                }

                //

                //GSM
                if (gsmLevel || gsmBalance) {
                    if (gsmLevel) row.cart.cache.Gsm.push(gsmLevel);
                    if (gsmBalance) row.cart.cache.Gsm.push(gsmBalance);
                }

                // Ячейка Показание - из кэша
                if (row.column_record_type && row.column_parameter) {
                    var c = row.cache[row.column_record_type]
                    if (c && what.call(c) === "[object Array]") {
                        for (var i = 0; i < c.length; i++) {
                            var par = c[i];
                            if (par && (par.s1 == row.column_parameter)) {
                                row.cart.lastValue = par;
                            }
                        }
                    }
                } //else if (row.cache.Current && row.cache.Current.length > 0) {
                //    row.cart.lastValue = row.cache.Current[0];//заглушка
                //}



                // разница во времени м/у сервером и прибором
                if (row.cache.Current && row.cache.Current.length > 0) {
                    var dt = new Date(row.cache.Current[0].dt1);
                    var date = new Date(row.cache.Current[0].date);
                    var pos = dt < date;
                    var diff = pos ? date - dt : dt - date;
                    var secs = Math.floor(diff / 1000);
                    var mins = Math.floor(secs / 60);
                    var hours = Math.floor(mins / 60);
                    var days = Math.floor(hours / 24);
                    var hh = hours - (days * 24);
                    var mm = mins - (hours * 60);
                    var ss = secs - (mins * 60);
                    //var text = (pos ? "+" : "-") + " " + (days > 0 ? days + " дней " : "") + hh + ":" + mm + ":" + ss;
                    var text = (pos ? "+" : "-") + " " + (days > 0 ? days + " дней " : "") + hh + " ч. " + mm + " мин.";
                    row.cart.diff = { pos: pos, days: days, hh: hh, mm: mm, ss: ss, text: text };
                }
            }

            // PARAMETER Параметры - теггирован или нет
            if (row.Parameter && row.Parameter.length > 0) {
                row.cart.isTagged = $helper.rowParameterIsTagged(row);
            }
            //
            var phones = {};
            if (row.CsdConnection) {
                for (var i = 0; i < row.CsdConnection.length; i++) {
                    var con = row.CsdConnection[i];
                    if (con.phone) {
                        phones[con.phone] = 1;
                    }
                }
            }
            if (row.MatrixConnection) {
                for (var i = 0; i < row.MatrixConnection.length; i++) {
                    var con = row.MatrixConnection[i];
                    if (con.phone) {
                        phones[con.phone] = 1;
                    }
                }
            }
            if (row.HttpConnection) {
                for (var i = 0; i < row.HttpConnection.length; i++) {
                    var con = row.HttpConnection[i];
                    if (con.phone) {
                        phones[con.phone] = 1;
                    }
                }
            }
            row.cart.Phone = $helper.assocToArray(phones);

            //

            row.cart.references = [];//for reports

            if (row.class && row.class != "") {
                row.cart.references.push(row.class);
            }

            if (row.Device) {
                var asc = {};
                for (var i = 0; i < row.Device.length; i++) {
                    var dev = row.Device[i];
                    //DEV ID
                    if (dev.id && !asc[dev.id]) {
                        row.cart.references.push(dev.id);
                        asc[dev.id] = 1;
                    }
                    //DEV REFERENCE
                    if (dev.reference && !asc[dev.reference]) {
                        row.cart.references.push(dev.reference);
                        asc[dev.reference] = 1;
                    }
                }
            }

            //

            row.cart.done = true;
        }

        //
        wrapped.push(row.id);
        $timeout.cancel(wrappedTimeout);
        wrappedTimeout = $timeout(wrappedOk, 10);

        return row;
    }


    function wrapDelayed() {
        $rootScope.$broadcast("list:update-done", null);
        $log.debug("delayed wrap of " + toWrap.length);
        var buf = [];
        for (var i = 0; i < toWrap.length; i++) {
            buf.push(cacheSvc.get(toWrap[i]));
        }
        toWrap.length = 0;
        //
        for (var i = 0; i < buf.length; i++) {
            var row = buf[i];
            if ($parse('id')(row) != undefined) {
                cacheSvc.set(row.id, wrap(row));
            }
        }
    }

    function wrappedOk() {
        $rootScope.$broadcast("list:update-done", null);
        $log.debug("wrap done of " + wrapped.length);
        wrapped.length = 0;
    }

    var updateIds = function (ids) {
        return $transport.send(new Message({ what: "rows-get" }, { ids: ids }))
            .then(function (message) {
                $log.debug("list-svc ответ с сервера %s", message.head.what);
                if (message.head.what == "rows-get") {
                    var rows = $parse('body.rows')(message) || [];
                    $log.debug("list-svc get rows " + rows.length);
                    for (var i = 0; i < rows.length; i++) {
                        var row = rows[i];
                        if ($parse('id')(row) != undefined) {
                            cacheSvc.set(row.id, wrapWithDelay(row));
                        }
                    }
                } else {
                    return $q.reject($parse('body.message')(message) || "неизвестная ошибка");
                }
            });
    }


    var updateListIds = function (ids) {

        service.list = [];
        if (!ids) return;

        for (var i = 0; i < ids.length; i++) {
            var id = ids[i];
            var row = cacheSvc.get(id);
            service.list.push(row);
        }

    }

    var getAllIds = function () {

        service.groups.length = 0;

        var groups = $folders.selected();

        var groupIds = [];

        if (groups.length == 0) {
            service.groups.push({ id: "root", name: "Всё" });
        } else {
            for (var i = 0; i < groups.length; i++) {
                var g = groups[i];
                if (g && g.id) {
                    groupIds.push(g.id);
                    service.groups.push(g);
                }
            }
        }

        return $transport.send(new Message({ what: "rows-get-ids" }, { filter: { text: service.filter, groups: groupIds } }))
            .then(function (message) {
                $log.debug("list-svc ответ с сервера %s", message.head.what);
                if (message.head.what == "rows-get-ids") {
                    var ids = $parse('body.ids')(message) || [];
                    var filterText = $parse('body.filter.text')(message) || "";
                    $log.debug("list-svc get ids " + ids.length + " фильтр " + filterText);

                    //применение пинов

                    var asc = {};
                    var idsPin = [];
                    var idsWithoutPin = [];
                    //var idsWithPin = [];

                    //ассоциативный массив
                    for (var i = 0; i < service.pins.length; i++) {
                        var pin = service.pins[i];
                        asc[pin.id] = 1;
                    }

                    //формируем список-без-пинов
                    for (var i = 0; i < ids.length; i++) {
                        var id = ids[i];
                        if (asc[id]) {
                            asc[id] = 2;
                            continue;
                        }
                        idsWithoutPin.push(id);
                    }

                    //формируем список-только-пинов
                    for (var i = 0; i < service.pins.length; i++) {
                        var pin = service.pins[i];
                        if (asc[pin.id] == 2) {
                            idsPin.push(pin.id);
                        }
                    }

                    //объединение списков
                    for (var i = 0; i < idsWithoutPin.length; i++) {
                        var id = idsWithoutPin[i];
                        idsPin.push(id);
                    }

                    updateListIds(idsPin);

                    return message.body;
                } else {
                    return $q.reject($parse('body.message')(message) || "неизвестная ошибка");
                }
            });
    }

    var getPinnedIds = function () {

        var ids = [];

        for (var i = 0; i < service.pins.length; i++) {
            var pin = service.pins[i];
            ids.push(pin.id);
        }

        updateListIds(ids);

        return $q.when({ ids: ids });
    }


    service.getIds = function () {
        var p;
        if (!service.isPinnedOnly) {
            p = getAllIds();
        } else {
            p = getPinnedIds();
        }
        return p;
    }

    //
    service.part = function (skip, take) {

        var min = function (a, b) { return a < b ? a : b; }
        var plist = [];
        var pids = [];

        for (var i = skip; i < min(service.list.length, skip + take) ; i++) {
            var row = service.list[i];
            plist.push(row);
            if (row.type == "Undefined") {
                pids.push(row.id);
            }
        }

        if (pids.length > 0) {
            updateIds(pids);
        }

        return $q.when({ rows: plist, count: service.list.length });
    };

    service.getRow = function (id) {
        return cacheSvc.get(id);
    }

    service.getList = function () {
        return service.list;
    };

    //var getRow = function (id) {
    //    for (var i = 0; i < service.list.length; i++) {
    //        var item = service.list[i];
    //        if (item.id === id) return item;
    //    }
    //};

    var cacheClean = function () {
        var listIds = [];
        for (var i = 0; i < service.list.length; i++) {
            var elem = service.list[i];
            if (elem && elem.id) {
                listIds.push(elem.id);
            }
        }
        cacheSvc.remove(listIds);
    }

    service.cacheClean = cacheClean;

    //

    ($rootScope.$on("transport:message-received-old", function (e, message) {

        var ids = [];

        if (message.head.what == "changes") {
            var rules = message.body.rules;
            for (var i = 0; i < rules.length; i++) {
                var rule = rules[i];
                if (rule.target == "node" && rule.content && rule.content.type == "Tube") {
                    ids.push(rule.content.id);
                }
            }
        }

        if (message.head.what == "ListUpdate") {
            ids = message.body.ids;
        }

        if (ids.length > 0) {
            $log.debug("пришло обновлений строк %s", ids.length);

            var pids = [];

            for (var i = 0; i < ids.length; i++) {
                var id = ids[i];
                if (cacheSvc.get(id).type != "Undefined") {
                    pids.push(id);
                }
            }

            if (pids.length > 0) {
                updateIds(pids);
            }
        }

    }));

    //

    service.select = function (ids) {
        service.sels.length = 0;
        for (var i = 0; i < ids.length; i++) {
            var id = ids[i];
            var row = cacheSvc.get(id);
            service.sels.push(row);
        }
    }

    service.getSelected = function () {
        return service.sels;
    }

    service.getPinned = function () {
        return service.pins;
    }

    service.getSelectedIds2 = function () {
        var ids = [];
        for (var i = 0; i < service.sels.length; i++) {
            var sel = service.sels[i];
            if (sel && sel.id) {
                ids.push(sel.id);
            }
        }
        return ids;
    }


    var selectedIds = [];
    service.getSelectedIds = function () {
        return selectedIds;
    }

    service.setSelectedIds=function(ids){
        selectedIds = ids;        
        $rootScope.$broadcast("list:selection-changed", null);
    }

    service.getGroups = function () {
        return service.groups;
    }

    service.setFilterText = function (flt) {
        if (service.filter != flt) {
            service.filter = flt;
            $rootScope.$broadcast("list:filter-changed", null);
        }
    }

    service.getPinnedOnly = function () {
        return service.isPinnedOnly;
    }

    service.setPinnedOnly = function (isPinnedOnly) {
        if (service.isPinnedOnly != isPinnedOnly) {
            service.isPinnedOnly = isPinnedOnly;
            $rootScope.$broadcast("list:filter-changed", null);
        }
    }

    service.pin = function (ids) {
        service.pins.length = 0;
        for (var i = 0; i < ids.length; i++) {
            var id = ids[i];
            var row = cacheSvc.get(id);
            service.pins.push(row);
        }
    }

    service.init = function () {
        carts.length = 0;
        service.filter = "";
        cacheClean();
        service.sels.length = 0;
        service.pins.length = 0;
        service.list.length = 0;
        service.groups.length = 0;
        service.isPinnedOnly = false;
    }

    $rootScope.$on("auth:deauthorized", function (e, message) {
        service.init();
    });
})


