    function structureUpdate() {
        $log.debug("list-ctrl structure-update");

        //включаем оверлей "загрузка данных"
        if (!model.isLoading) {
            model.isLoading = true;
            $scope.opt.api.showLoading(model.isLoading);
        }

        //снятие выбора и обнуление всего
        uncheckAll();
        model.counting = 0;
        model.count = 0;

        //обновление данных
        $list.getIds().then(function (data) {
            //сброс оверлея
            if (model.isLoading) {
                model.isLoading = false;
                $scope.opt.api.showLoading(model.isLoading);
            }
            $log.debug("list-ctrl structure UPDATED список получен: " + data.ids.length);
            //обновление datasource
            $scope.opt.api.setDatasource(datasource);
            //обновление статусной строки
            statusBarUpdate();
        }, function (err) {
            $log.error("list-ctrl structure NOT updated " + err);
        });
    }

    function statusBarUpdate() {
        var fs = $list.getGroups();
        model.foldersShownNames.length = 0;
        var isFolderRoot = false;
        for (var i = 0; i < fs.length; i++) {
            var row = fs[i];
            if (row.id == 'root') {
                isFolderRoot = true;
                break;
            }
            model.foldersShownNames.push(row.name);
        }
        //root-папка
        if (isFolderRoot) {
            model.foldersShownNames.length = 0;
            model.foldersShownNames.push(row.name);
        }
    }

