    var upd = function (filter) {
        $scope.model.grid2.api.showLoading(true);
        selectedIds = {};

        var datasource = {
            pageSize: 100,
            overflowSize: 100,
            maxConcurrentRequests: 1,
            maxPagesInCache: 2,
            getRows: function (params) {
                var current = params.startRow;
                var take = params.endRow - params.startRow;

                filter.page = {
                    offset: current,
                    count: take
                };

                filter.order = [];
                for (var i = 0; i < params.sortModel.length; i++) {
                    var sm = params.sortModel[i];
                    filter.order.push({
                        column: sm.field,
                        dir: sm.sort
                    });
                }

                $transport.send(new Message({ what: "rows-get-2" }, { filter: filter })).then(function (message) {
                    $scope.model.grid2.api.showLoading(false);
                    var rows = message.body.rows;
                    model.count = message.body.count;
                    params.successCallback(rows, model.count);
                    datasource.rowCount = model.count;

                    visibleRows.length = 0;
                    visibleIds = {};
                    for (var i = 0; i < rows.length; i++) {
                        var id = rows[i].id;
                        visibleRows.push(rows[i]);
                        visibleIds[id] = true;
                    }

                    //update selection
                    updateSelection();
                });
            }
        };
        $scope.model.grid2.api.setDatasource(datasource);
    }

